import { useEffect, useState } from "react"


function Content(){

 const [countdown, setCountdown ] = useState(180)
 const handleCountdown = useEffect(() => {
  setInterval(() =>{
    setCountdown(countdown-1);
    console.log(countdown);
   },1000)
},[countdown])

  return (
    <div>
      <button onClick={handleCountdown} style={{margin: 20} }>Play</button>
      <h1 style={{margin: 20} }>{countdown}</h1>
    </div>
  )
}


export default Content;
